function operatorType(value) {
    if(value.indexOf("*")!=-1 ) {
        return " LIKE ";
    } else {
        return " = ";
    }
}
var log = mc.getServiceLog();
var where = " WHERE applicationGroup.valid=1 ";
var innerJoin = "";
where += addWhere("id","Integer","applicationGroup","id");
where += addWhere("code","String","applicationGroup","code");
where += addWhere("name","String","applicationGroup","name");
where += addWhere("endpointType","String","applicationGroup","endpoint_type");
where += addWhere("ordinal","Integer","applicationGroup","ordinal");
where += addWhere("valid","Integer","applicationGroup","valid");
where += addWhere("numberRetries","Integer","applicationGroup","number_retries");
where += addWhere("timeBetweenRetries","Integer","applicationGroup","time_between_retries");
where += addWhere("errorsNotRequireTicket","String","applicationGroup","errors_not_require_ticket");
mc.setProperty("uri.var.whereclause",where);
mc.setProperty("uri.var.innerjoinclause",innerJoin);

if(!mc.getProperty("uri.var.orderby")){
    mc.setProperty("uri.var.orderby","applicationGroup.id");
} else {
    mc.setProperty("uri.var.orderby","applicationGroup."+mc.getProperty("uri.var.orderby"));
}

// FROM TO
var fromto = "OFFSET "+mc.getProperty("uri.var.from")+" ROWS FETCH NEXT "+mc.getProperty("uri.var.to")+" ROWS ONLY";
