function operatorType(value) {
    if(value.indexOf("*")!=-1 ) {
        return " LIKE ";
    } else {
        return " = ";
    }
}
var log = mc.getServiceLog();
var where = " WHERE applicationSimple.valid=1 ";
var innerJoin = "";
where += addWhere("id","Integer","applicationSimple","id");
where += addWhere("code","String","applicationSimple","code");
where += addWhere("name","String","applicationSimple","name");
where += addWhere("automaticProvision","Integer","applicationSimple","automatic_provision");
where += addWhere("status","String","applicationSimple","status");
where += addWhere("valid","Integer","applicationSimple","valid");
where += addWhere("applicationGroupId","Integer","applicationSimple","application_group_id");
where += addWhere("parentId","Integer","applicationSimple","parent_id");
mc.setProperty("uri.var.whereclause",where);
mc.setProperty("uri.var.innerjoinclause",innerJoin);

if(!mc.getProperty("uri.var.orderby")){
    mc.setProperty("uri.var.orderby","applicationSimple.id");
} else {
    mc.setProperty("uri.var.orderby","applicationSimple."+mc.getProperty("uri.var.orderby"));
}

// FROM TO
var fromto = "OFFSET "+mc.getProperty("uri.var.from")+" ROWS FETCH NEXT "+mc.getProperty("uri.var.to")+" ROWS ONLY";
mc.setProperty("uri.var.fromto",fromto);
