where += addWhere("dateCreated","DateTime","profilesSynchronization","date_created");
where += addWhere("dateSynchronization","DateTime","profilesSynchronization","date_synchronization");
where += addWhere("type","String","profilesSynchronization","type");
where += addWhere("status","String","profilesSynchronization","status");
where += addWhere("transactionalProfileId","Integer","profilesSynchronization","transactional_profile_id");
where += addWhere("globalProfileId","Integer","profilesSynchronization","global_profile_id");
where += addWhere("profileSynchronizationExecutionId","Integer","profilesSynchronization","profile_synchronization_execution_id");
where += addWhere("valid","Integer","profilesSynchronization","valid");
mc.setProperty("uri.var.whereclause",where);
mc.setProperty("uri.var.innerjoinclause",innerJoin);

if(!mc.getProperty("uri.var.orderby")){
    mc.setProperty("uri.var.orderby","profilesSynchronization.id");
} else {
